actionConfiguration.body:
() => {
		let geo_regions = GeoRegion.data;
		let vehicles_data = VehicleTypeData.data;
		let table_data = GoodwillConfig.data;
		table_data.map(function(config){
			 let geo_name = geo_regions.filter(d => d.id == config['city_id'])
			 config['city'] =  typeof geo_name[0] != 'undefined' ? geo_name[0].name : "Not Defined";

			 let vehicle_name = vehicles_data.filter(d => d.id == config['vehicle_id'])
			 config['vehicle'] =  typeof vehicle_name[0] != 'undefined' ? vehicle_name[0].vehicle_type : "Not Defined";

			 return config;
		})

		return table_data;

	}
